mapper.update:

UPDATE   COMPANY
   SET   COM_NAME   = #{ com_name },
         COM_NUM    = #{ com_num  }, 
         COM_BOSS   = #{ com_boss },
         COM_PW     = #{ com_pw   }   

   WHERE   COM_ID     = #{ com_id   }

mapper.delete:

	   DELETE 
	   FROM  COMPANY  
         WHERE   COM_ID = #{ com_id}
