mapper.insert:
      INSERT INTO RESUME (RE_NUM
						, USER_ID
						, USER_TITLE
						, SKILL
						, CAREER
						, REGION
						, USER_PORT
						, USER_INTTI
						, USER_INTCO
						 )
				VALUES ( (SELECT  NVL(MAX(RE_NUM), 0)+1 FROM  RESUME )
						, #{ user_id    }
						, #{ user_title }
						, #{ skill      }
						, #{ career     }
						, #{ region     }
						, #{ user_port  }
						, #{ user_intti }
						, #{ user_intco }
						 )
